/* globals Image */
/**
 * Test
 * @property
 */
var pwentk = {
	/**
	 * Test
	 */
		 Sprite: class{
		/**
		 * Test
		 */
		 constructor(name, visible, shapes, shapeNumber, scaleX, scaleY, x, y, z, rotation){
		 	this.setName(name, false);
		 	this.setVisible(visible || true, false);
		 	this.shapes = shapes || [{name:"Sample image",img:this.genImg("https://playentry.org/lib/entry-js/images/media/entrybot1.png")}];
		 	this.setShape(shapeNumber || 0, false);
		 	this.setScaleX(scaleX || 1, false);
		 	this.setScaleY(scaleY || 1, false);
		 	this.setX(x || 0, false);
		 	this.setY(y || 0, false);
		 	this.setZ(z || 0, false);
		 	this.setRotation(rotation || 0, false);
		 }

		 setScene(scene){
		 	this.scene = scene;
		 }
		 setName(name, doEvent = true){
		 	if(!name){
		 		this.setName(`스프라이트 ${pwentk.genId()}`, doEvent);
		 		pwentk.genErr(22,this);
			}/*else if(pwentk.sprites.some(val=>{return val.name==String(name);})){
				this.setName(`스프라이트 ${this.id}`, doEvent);
				pwentk.genErr(11,this);
			}*/else{
				this.name = String(name);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		show(doEvent = true){
			this.visible = true;
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		hide(doEvent = true){
			this.visible = false;
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setVisible(boolean, doEvent = true){
			this.visible = Boolean(boolean);
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setShape(n, doEvent = true){
			if(!isNaN(n)){
				if(this.shapes[Number(n)]){
					this.shapeNumber = Number(n);
				}else{
					pwentk.genErr(14,this);
				}
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		getShape(){
			return this.shapes[this.shapeNumber];
		}
		setScaleX(scaleX, doEvent = true){
			if(!isNaN(scaleX)){
				this.scaleX = Number(scaleX);
			}else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setScaleY(scaleY, doEvent = true){
			if(!isNaN(scaleY)){
				this.scaleY = Number(scaleY);
			}else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		//좌표
		setX(x, doEvent = true){
			if(!isNaN(x)){
				this.x = Number(x);
			}else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setY(y, doEvent = true){
			if(!isNaN(y)){
				this.y = Number(y);
			}else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setZ(z, doEvent = true){
			if(!isNaN(z)){
				this.z = Number(z);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		setRotation(rotation, doEvent = true){
			if(!isNaN(rotation)){
				this.rotation = Number(rotation);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		addX(x, doEvent = true){
			if(!isNaN(x)){
				this.x += Number(x);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		addY(y, doEvent = true){
			if(!isNaN(y)){
				this.y += Number(y);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		addZ(z, doEvent = true){
			if(!isNaN(z)){
				this.z += Number(z);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}
		addRotation(rotation, doEvent = true){
			if(!isNaN(rotation)){
				this.rotation += Number(rotation);
			}
			else{
				pwentk.genErr(21,this);
			}
			if(doEvent){pwentk.fire("changed", this);}
			return this;
		}

		genImg(src){
			let img = new Image();
			img.src = src;
			return img;
		}

	},
	BlockSet: class{
		constructor(options){
			this.name = options.name;
			this.template = options.template;
			this.func = options.func;
			this.param = 
			pwentk.nArray(this.template.match(/(?<=\(\()(.*?)(?=\)\))/g))
			.map(name => ({name: name, type: "string", data: "0"}))
			.concat(
				pwentk.nArray(this.template.match(/(?<=\<\<)(.*?)(?=\>\>)/g))
				.map(name => ({name: name, type: "boolean", data: true}))
				)
			.concat(
				pwentk.nArray(this.template.match(/(?<=\{\{)(.*?)(?=\}\})/g))
				.map(name => ({name: name, type: "function", data: function(){}}))
				);
		}
	},
	Block: class{
		constructor(parent){
			this.name = parent.name;
			this.template = parent.template;
			this.param = parent.param;
			this.innerBlock = this.param.filter(val=>val.type=="function")
		}
		getPath(text){
			var output = "";
			output += `M0 0 l 5 5 v -4 
			a 1 1 90 0 1 1 -1  
			h 70
			a 5 5 180 0 1 0 10 `;
			output += `H 11 `;

			if(this.innerBlock.length > 0){
				output += `a 1 1 90 0 0 -1 1
				v 4
				l -5 -5`;
				for(var i=0;i<this.innerBlock.length-1;i++){
					output += `v 10 
					l 5 5 v -4 
					a 1 1 90 0 1 1 -1  
					h 20 
					a 5 5 180 0 1 0 10 
					H 11 
					a 1 1 90 0 0 -1 1 
					v 4 
					l -5 -5 `;
				}
				output += `v 10 
				l 5 5 v -4 
				a 1 1 90 0 1 1 -1  
				h 20 
				a 5 5 180 0 1 0 10 `;
			}
			output += `H 6 a 1 1 90 0 0 -1 1 v 5 l -5 -5
			Z`;
			return output;
		}
	},
	Scene: class{
		constructor(options){
			this.name = options.name;
			this.sprites = [];
		}

		newSprite(sprite){
			sprite.setScene(this);
			this.sprites.push(sprite);
			pwentk.fire("newSprite", sprite);
		}
	},
	Project: class{
		constructor(options){
			this.name = options.name;
			this.scenes = [new pwentk.Scene({"name": "장면 1"})];
			this.nowSceneNo = 0;
			pwentk.fire("sceneChanged", this);
		}
		setNowScene(sceneNo){
			this.nowSceneNo = sceneNo;
			pwentk.fire("sceneChanged", this);
		}
		nowScene(){
			return this.scenes[this.nowSceneNo];
		}
		newScene(name){
			this.scenes.push(new pwentk.Scene({"name": name}));
		}
		deleteScene(sceneNo){
			this.scenes.splice(sceneNo, 1);
			if(this.nowSceneNo > this.scenes.length - 1){
				this.setNowScene(this.scenes.length - 1);
			}else if(this.nowSceneNo > sceneNo){
				this.setNowScene(this.nowSceneNo - 1);
			}
		}
	},
	genErr: function(errCode, errSprite){
		if(errSprite){
			console.warn(new Error(`"${errSprite.name}" 스프라이트에서 에러 #${errCode} : "${pwentk.errorCode[errCode]}"`));
		}else{
			console.warn(new Error(`에러 #${errCode} : "${pwentk.errorCode[errCode]}"`));
		}
	},
	errorCode: {
		"10": "잘못된 참조",
		"11": "중복된 스프라이트 이름",
		"12": "존재하지 않는 스프라이트 번호",
		"13": "존재하지 않는 스프라이트 이름",
		"14": "존재하지 않는 모양 번호",
		"15": "존재하지 않는 모양 이름",

		"20": "잘못된 입력 형식",
		"21": "잘못된 입력 형식 (Number여야 함)",
		"22": "스프라이트 이름이 입력되지 않음",
		"23": "잘못된 입력 형식 (String여야 함)"
	},
	event: {
		newSprite: [],
		changed: [],
		sceneChanged: []
	},
	on: function(eventName, callback){
		this.event[eventName].push(callback);
	},
	fire: function(eventName, param){
		console.log(`"${eventName}" event fired`);
		for(var i in this.event[eventName]){
			this.event[eventName][i](param);
		}
	},
	getSprite: function(scene, info, option = "name"){
		switch(info.constructor.name){
			case "Number":{
				let returnTemp = scene.sprites[info];
				if(returnTemp){
					return returnTemp;
				}else{
					pwentk.genErr(12);
				}
				break;
			}
			case "String":{
				let returnTemp = scene.sprites.filter(val => {return val[option] == info;})[0];
				if(returnTemp){
					return returnTemp;
				}else{
					pwentk.genErr(13);
				}
				break;
			}
		}
	},
	nArray: arr => arr?arr:[],
	genId: () => Math.random().toString(36).substr(2, 5)
};

/* globals fabric */
/* globals pwentk */
var canvas = new fabric.Canvas("mainCanvas");
canvas.setDimensions({width: 1920, height: 1080},{backstoreOnly:true});

fabric.Object.prototype.cornerSize = 30;
fabric.Object.prototype.transparentCorners = false;
fabric.Object.prototype.borderScaleFactor = 5;
fabric.Object.prototype.rotatingPointOffset = 120;
fabric.Object.prototype.borderColor = "rgba(235,88,171,0.75)";
fabric.Object.prototype.cornerColor = "rgba(235,88,171,0.75)";

var pwentkFabric = {
	newSprite:function(sprite){
		let spriteImg = sprite.getShape().img;
		if(sprite.getShape().loaded){
			canvas.add(new fabric.Image(spriteImg, pwentkFabric.spriteToObject(sprite)));
		}else{
			spriteImg.onload = function(){
				canvas.add(new fabric.Image(spriteImg, pwentkFabric.spriteToObject(sprite)));
				sprite.getShape().loaded = true;
				pwentkFabric.spriteReDraw(sprite);
			};
		}
	},
	spriteToObject:function(sprite){
		return {
			sprite: sprite,
			scaleX: sprite.scaleX,
			scaleY: sprite.scaleY,
			left: sprite.x+ canvas.width/2,
			top: -sprite.y+ canvas.height/2,
			angle: sprite.rotation,
			originX: "center",
			originY: "center"
		};
	},
	spriteReDraw:function(sprite){
		let targetObject = canvas.getObjects().filter(val => {return val.sprite == sprite;})[0];
		if(targetObject){
			if(sprite.visible && sprite.scene == main.nowScene()){
				targetObject.set(pwentkFabric.spriteToObject(sprite));
			}else{
				canvas.remove(targetObject);
			}
		}else if(sprite.visible && sprite.scene == main.nowScene()){
			pwentkFabric.newSprite(sprite);
		}
		canvas.renderAll();
	}
};

pwentk.on("newSprite", pwentkFabric.newSprite);

pwentk.on("changed", pwentkFabric.spriteReDraw);

pwentk.on("sceneChanged", function(project){
	console.log(canvas.getObjects());
	canvas.remove(...canvas.getObjects());
	for(var i in main.nowScene().sprites){
		if(main.nowScene().sprites[i].visible){
			pwentkFabric.newSprite(main.nowScene().sprites[i]);
		}
	}
	canvas.renderAll();
});
canvas.on("object:modified", function(event){
	var sprite = event.target.sprite;
	if(sprite){
		sprite
		.setScaleX(event.target.scaleX, false)
		.setScaleY(event.target.scaleY, false)
		.setX(event.target.left- canvas.width/2, false)
		.setY(-event.target.top+ canvas.height/2, false)
		.setRotation(event.target.angle);
	}
});

